
public class ex406 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("구구단을 세자~\n");
		for(int i = 1; i<10; i++) {
			System.out.println(i + "단: ");
			
			for(int j=1; j<10; j++) {
				System.out.println(i + "x" + j + "=" + i*j);
			}
			System.out.println("\n");
		}
	}

}


